package org.examples.day07;
public class ArithmeticOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte a=5;
		byte b=2;
		
		System.out.println("a+b= "+ (a+b));
		System.out.println("a-b= "+ (a-b));
		System.out.println("a*b= "+ (a*b));
		System.out.println("a/b= "+ (a/b));
		System.out.println("a%b= "+ (a%b));
	}

}
