package org.examples.day08;

public class ConditionIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		   if (a == 5) {
				System.out.println("Value of a is 5");
		   }
		   
		   else {
				System.out.println("Value of a is not 5");
				
		   }
	}

}
